mapper.select:
SELECT tcrl.*, tc.customer_name, tu.true_name
FROM t_customer_return_list tcrl
LEFT JOIN t_customer tc
ON tcrl.customer_id = tc.customer_id
LEFT JOIN t_user tu
ON tcrl.user_id = tu.user_id


        tcrl.returnNumber=#{returnNumber}


        AND tcrl.customerId like #{customerId}


        AND tcrl.state like #{state}


        AND tcrl.sTime like #{sTime}


        AND tcrl.eTime like #{eTime}
